// SPDX-License-Identifier: MIT
pragma solidity >=0.4.22 <0.9.0;

contract CorporateVoting {
  constructor() public {
  }

  struct Company {
    bytes32 cin;
    bytes32 name;
    address[] employees;
    address admin;
  }

  struct UserDetailsWithCompanyId {
    bool isAdmin;
    uint cid;
    bytes32 email;
  }

  struct User {
    bytes32 name;
    UserDetailsWithCompanyId[] companies;
  }

  mapping(address => User) users;
  Company[] companies;

  function getUser() public view returns (bytes32 name, address add) {
    return (users[msg.sender].name, msg.sender);
  }

  function setUser(bytes32 mName) public returns (bytes32 name, address add) {
    users[msg.sender].name = mName;
    return (mName, msg.sender);
  }

  /*function getAllEmployees(uint cid) public view returns (address[] employees) {
    return (companies[cid].employees);
  }*/
}
